import { useState } from "react";
import React from "react";
import axios from "axios";
import HeaderLinks from "./HeaderLinks.js";

function Header() {
  return (
    <div className="header-wrapper">
      <h1>Budget</h1>
      <HeaderLinks />
    </div>
  );
}

function ManageGroups() {
  let baseURL = "http://localhost:3000/dataManageGroups.json";
  const [data, setData] = useState(null);
  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setData(response.data);
    });
  }, [baseURL]);

  if (!data) return "error";

  const handleGroupDelete = (event) => {
    alert("delete id:" + event.target.id);
  };

  const handleGroupEdit = (event) => {
    alert("edit id:" + event.target.id);
  };
  const handleManageGroupAdd = (event) => {};

  return (
    <>
      <div id="ManageGroups" key="ManageGroups" className="is-hidden">
        <div className="top-padding left-indent card card-body bg-light">
          {data.map((item) => {
            return (
              <>
                <div className="row">
                  <div className="manage-groups-item-wrapper">
                    <button
                      id={"GroupDelete-" + item.id}
                      key={"GroupDelete-" + item.id}
                      className="button-href"
                      onClick={handleGroupDelete}
                    >
                      delete
                    </button>
                    <button
                      id={"GroupEdit-" + item.id}
                      key={"GroupEdit-" + item.id}
                      className="button-href"
                      onClick={handleGroupEdit}
                    >
                      edit
                    </button>
                    &nbsp;&nbsp;
                    {item.name}
                  </div>
                </div>
              </>
            );
          })}
          <label for="ManageGroup-Submit">
            <strong>New Group</strong>
          </label>
          <div className="manage-groups-input-wrapper">
            <input
              type="text"
              id="ManageGroups-Input"
              key="ManageGroups-Input"
              className="col-md-2"
            />
            <button
              type="submit"
              id="ManageGroup-Submit"
              key="ManageGroup-Submit"
              className="col-md-1"
              onClick={handleManageGroupAdd}
            >
              add
            </button>
          </div>
          <div
            id="ManageGroups-Note"
            key="ManageGroups-Note"
            className="manage-groups-note"
          >
            Note: Deleting a group does not delete the envelopes within the
            group.
          </div>
        </div>
      </div>
    </>
  );
}
function AddEnvelope() {
  const handleAddEnvelopeAdd = (event) => {
    alert("add envelope");
  };

  return (
    <>
      <div id="AddEnvelope" key="AddEnvelope" className="is-hidden">
        <div className="top-padding left-indent card card-body bg-light">
          <div className="row">
            <div className="manage-groups-input-wrapper">
              <input
                type="text"
                id="ManageGroups-Input"
                key="ManageGroups-Input"
                className="col-md-2"
              />
              <button
                type="submit"
                id="ManageGroup-Submit"
                key="ManageGroup-Submit"
                className="col-md-1"
                onClick={handleAddEnvelopeAdd}
              >
                add
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
function Transfer() {
  return <></>;
}

function Subheader() {
  const handleManageGroups = () => {
    let e = document.getElementById("ManageGroups");
    if (e.classList.contains("is-hidden")) e.classList.remove("is-hidden");
    else e.classList.add("is-hidden");
  };
  const handleAddEnvelope = () => {
    let e = document.getElementById("AddEnvelope");
    if (e.classList.contains) e.classList.remove("is-hidden");
    else e.classList.add("is-hidden");
  };
  const handleTransfer = () => {
    let e = document.getElementById("Transfer");
    if (e.classList.contains) e.classList.remove("is-hidden");
    else e.classList.add("is-hidden");
  };

  return (
    <div className="subheader-wrapper">
      <div className="subheader-buttons">
        <button onClick={handleManageGroups}>manage groups</button>&nbsp;&nbsp;
        <button onClick={handleAddEnvelope}>add envelope</button>&nbsp;&nbsp;
        <button onClick={handleTransfer}>transfer</button>&nbsp;&nbsp;
      </div>
      <div className="subItems finances-well">
        <ManageGroups />
        <AddEnvelope />
        <Transfer />
      </div>
    </div>
  );
}

function Footer() {
  return (
    <div className="footer-wrapper">
      <h1>Footer</h1>
    </div>
  );
}

function Items({ originalData }) {
  const [data, setData] = useState(originalData);

  return data.map((item, index) => {
    return (
      <Item
        id={item.id}
        title={item.title}
        currentBalance={item.current}
        budgetBalance={item.budget}
        arrayIndex={index}
        setData={setData}
        data={data}
      />
    );
  });
}

function Controls({ id, handleDeleteClick, handleEditClick, titleField }) {
  return (
    <div id={id + "Title"} key={id + "Title"} className="item-title">
      <button
        key={id + "DeleteLink"}
        className="edit-delete-button"
        onClick={(event) => handleDeleteClick(event)}
      >
        <i className="bi bi-trash"></i> &nbsp;
      </button>
      <button
        key={id + "EditLink"}
        className="edit-delete-button"
        onClick={(event) => handleEditClick(event)}
      >
        <i className="bi bi-pencil-square"></i>
      </button>
      &nbsp;
      {titleField}
    </div>
  );
}

function Bar({ id, width }) {
  return (
    <div className="bar-wrapper">
      <div
        id={id + "BarCurrent"}
        key={id + "BarCurrent"}
        style={{ width: width + "%" }}
        className="item-current"
      ></div>
      <div
        id={id + "BarBudget"}
        key={id + "BarBudget"}
        style={{ width: 100 - width + "%" }}
        className="item-budget"
      ></div>
    </div>
  );
}

function Item({
  id,
  title,
  currentBalance,
  budgetBalance,
  arrayIndex,
  setData,
  data,
}) {
  const [edit, setEdit] = useState(false);
  let titleField = title;
  let budgetBalanceField = budgetBalance;

  let width = (currentBalance / budgetBalance) * 100;
  if (width > 99) width = 100;

  const saveEdit = (event) => {
    let localAccount = data.slice();
    localAccount[arrayIndex].title = document.getElementById(
      `${id}TitleEdit`
    ).value;
    localAccount[arrayIndex].budget = Number(
      document.getElementById(`${id}BudgetEdit`).value
    );
    setData(localAccount);
    setEdit(false);
    event.preventDefault();
  };

  if (edit) {
    titleField = (
      <>
        <input
          type="text"
          id={id + "TitleEdit"}
          key={id + "TitleEdit"}
          defaultValue={title}
          size="30"
        />
        <button
          type="submit"
          id={id + "TitleSave"}
          key={id + "TitleSave"}
          onClick={(event) => saveEdit(event)}
        >
          save
        </button>
      </>
    );
    budgetBalanceField = (
      <input
        type="number"
        id={id + "BudgetEdit"}
        key={id + "BudgetEdit"}
        defaultValue={budgetBalance}
        style={{ width: "70px" }}
      />
    );
  } else {
    titleField = title;
    budgetBalanceField = currentBalance + " of " + budgetBalance;
  }

  const handleEditClick = (event) => {
    if (edit) setEdit(false);
    else setEdit(true);
    event.preventDefault();
  };

  const handleDeleteClick = (event) => {
    let localAccount = data.slice();
    localAccount.splice(arrayIndex, 1);
    console.log(localAccount);
    setData(localAccount);
    event.preventDefault();
  };
  return (
    <>
      <div
        id={id + "WrapperDiv"}
        key={id + "WrapperDiv"}
        className="title-wrapper"
      >
        <Controls
          id={id}
          handleDeleteClick={handleDeleteClick}
          handleEditClick={handleEditClick}
          titleField={titleField}
        />

        <div id={id + "Balance"} key={id + "Balance"} className="item-balance">
          {budgetBalanceField}
        </div>
      </div>
      <Bar id={id} width={width} />
    </>
  );
}

function BudgetSection({ originalData }) {
  return (
    <div>
      <Header />
      <Subheader />
      <Items originalData={originalData} />
      <Footer />
    </div>
  );
}

export default function Budget() {
  let baseURL = "http://localhost:3000/data.json";
  const [data, setData] = useState(null);
  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setData(response.data);
    });
  }, [baseURL]);

  if (!data) return "error";

  return <BudgetSection originalData={data} />;
}
