import { useState } from "react";

function Header() {
  return (
    <>
      <h1>Budget</h1>
      <div className="header-links">
        <a href="./UploadBalances">Upload balances</a>&nbsp;&nbsp;
        <a href="./reports">Reports</a>
      </div>{" "}
    </>
  );
}

function Footer() {
  return <h1>Footer</h1>;
}

function Items() {
  return account.map((item) => {
    <Item
      id="{item.id}"
      title={item.name}
      currentBalance={item.current}
      budgetBalance={item.budget}
    />;
  });
}

function Item(props) {
  let width = (props.currentBalance / props.budgetBalance) * 100;
  if (width > 99) width = 100;
  return (
    <>
      <div className="title-wrapper">
        <div
          id={props.id + "Title"}
          key={props.id + "Title"}
          className="item-title"
        >
          {props.title}
        </div>
        <div
          id={props.id + "Balance"}
          key={props.id + "Balance"}
          className="item-balance"
        >
          {props.currentBalance + " of " + props.budgetBalance}
        </div>
        <div className="bar-wrapper"></div>
        <div
          id={props.id + "BarCurrent"}
          key={props.id + "BarCurrent"}
          style={{ width: width + "%" }}
          className="item-current"
        ></div>
        <div
          id={props.id + "BarBudget"}
          key={props.id + "BarBudget"}
          style={{ width: 100 - width + "%" }}
          className="item-budget"
        ></div>
      </div>
    </>
  );
}

function Budget() {
  return (
    <div>
      <Header key="1" />
      <Items key="2" />
      <Footer key="3" />
    </div>
  );
}

const account = [
  { id: 1, current: 150, budget: 300, name: "Auto" },
  { id: 2, current: 1500, budget: 1500, name: "Home loan" },
  { id: 3, current: 750, budget: 1000, name: "Groceries" },
  { id: 4, current: 250, budget: 300, name: "Travel" },
  { id: 5, current: 500, budget: 400, name: "Misc" },
];

export default function App() {
  return <Budget />;
}
