import { useState } from "react";

function ProductCategoryRow({ category }) {
  return (
    <tr>
      <th colSpan="2">{category}</th>
    </tr>
  );
}

function ProductRow({ product }) {
  const name = product.stocked ? (
    product.name
  ) : (
    <span style={{ color: "red" }}>{product.name}</span>
  );

  return (
    <tr>
      <td>{name}</td>
      <td>{product.price}</td>
    </tr>
  );
}

function ProductTable({ products }) {
  const rows = [];
  let lastCategory = null;

  products.forEach((product) => {
    if (product.category !== lastCategory) {
      rows.push(
        <ProductCategoryRow
          category={product.category}
          key={product.category}
        />
      );
    }
    rows.push(<ProductRow product={product} key={product.name} />);
    lastCategory = product.category;
  });

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Price</th>
        </tr>
      </thead>
      <tbody>{rows}</tbody>
    </table>
  );
}

function SearchBar({ onChangeEither }) {
  return (
    <form>
      <input
        type="text"
        id="inStockInput"
        placeholder="Search..."
        onChange={onChangeEither}
      />
      <label>
        <input type="checkbox" id="inStockCheckbox" onChange={onChangeEither} />{" "}
        Only show products in stock
      </label>
    </form>
  );
}

function FilterableProductTable({ products }) {
  const [filteredProducts, setFilteredProducts] = useState(products);

  const filterList = (event) => {
    let localProducts = {};
    localProducts = products;

    if (document.getElementById("inStockCheckbox").checked) {
      const results = localProducts.filter((item) => {
        if (document.getElementById("inStockCheckbox").checked)
          return item.stocked;
        else return true;
      });
      localProducts = results;
    }

    if (document.getElementById("inStockInput").value != "") {
      const results = localProducts.filter((item) => {
        return item.name.includes(
          document.getElementById("inStockInput").value
        );
      });
      localProducts = results;
    }
    setFilteredProducts(localProducts);
  };

  return (
    <div>
      <SearchBar onChangeEither={(event) => filterList(event)} />
      <ProductTable products={filteredProducts} />
    </div>
  );
}

const accounts = [
  { id: 1, current: 150, budget: 300, name: "Auto" },
  { id: 1, current: 150, budget: 300, name: "Auto" },
  { id: 1, current: 150, budget: 300, name: "Auto" },
  { id: 1, current: 150, budget: 300, name: "Auto" },
  { id: 1, current: 150, budget: 300, name: "Auto" },
];

export default function App() {
  return <FilterableProductTable products={PRODUCTS} />;
}
