import { useState, useEffect } from "react";
import React from "react";
import axios from "axios";
import { useSearchParams } from "react-router-dom";
import { format } from "date-fns";

function BarGraphMonthsBar({ className, amount, highest }) {
  return (
    <div
      className={className}
      style={{ height: (amount / highest) * 300 + "px" }}
    >
      {amount}
    </div>
  );
}

function BarGraphMonths({ data, highest }) {
  return data.map((item, index) => {
    return (
      <div className="wrapper-individual">
        <BarGraphMonthsBar
          className="actual"
          amount={item.current}
          highest={highest}
        />

        <BarGraphMonthsBar
          className="budget"
          amount={item.budget}
          highest={highest}
        />
        <div className="date-month">{item.date}</div>
      </div>
    );
  });
}

function IndividualBarGraphTotal({ amount, highestTotal, className }) {
  return (
    <>
      <div
        className={className}
        style={{ width: (amount / highestTotal) * 500 + "px" }}
      >
        {amount}
      </div>
    </>
  );
}

function BarGraphTotal({
  highestTotal,
  totalCurrent,
  totalBudget,
  categoryName,
}) {
  return (
    <>
      <div className="overall-balance">
        <span className="boldfont">{categoryName}</span> - overall balance since
        category inception: ${totalBudget - totalCurrent}
      </div>
      <div className="wrapper-total">
        <IndividualBarGraphTotal
          amount={totalCurrent}
          highestTotal={highestTotal}
          className="actual-total"
          total={totalCurrent}
        />

        <IndividualBarGraphTotal
          amount={totalBudget}
          highestTotal={highestTotal}
          className="budget-total"
          total={totalBudget}
        />
      </div>
    </>
  );
}

function DateRangeDropDownItem({ data, wrapper, id, type, onChangeHandler }) {
  let currentDate = "";
  const element = document.getElementById(id);
  const [searchParams, setSearchParams] = useSearchParams();
  if (element == null) {
    var selectList = document.createElement("select");
    selectList.id = id;
    selectList.onchange = onChangeHandler;
    wrapper.appendChild(selectList);

    data.map((item, index) => {
      var option = document.createElement("option");
      option.value = index;
      if (type == "cat") {
        option.text = item.category;
        if (
          searchParams.get("category") != null &&
          item.id == searchParams.get("category")
        )
          option.selected = true;
      } else {
        if (type == "dateFrom") {
          currentDate = String(Number(format(new Date(), "yyyy")) - 1);
          currentDate =
            String(Number(format(new Date(), "M")) + 1) + "/" + currentDate;
          option.text = item.date;
          if (currentDate == item.date) option.selected = true;
        } else {
          currentDate = format(new Date(), "M/yyyy");
          option.text = item.date;
          if (currentDate == item.date) option.selected = true;
        }
      }

      selectList.appendChild(option);
    });
  }
}

function DateRangeDropDown({}) {
  const [dataCat, setDataCat] = useState();
  const [data, setData] = useState();

  let baseURLCat = "http://localhost:3000/dataCategories.json";
  let baseURLData = "http://localhost:3000/dataReport.json";
  React.useEffect(() => {
    axios.get(baseURLCat).then((response) => {
      setDataCat(response.data);
    });
    axios.get(baseURLData).then((response) => {
      setData(response.data);
    });
  }, [baseURLCat, baseURLData]);
  if (!dataCat) return "error with categories";
  if (!data) return "error with dates";

  const onChangeHandler = () => {
    alert("asdf");
  };

  var wrapper = document.createElement("div");
  wrapper.classList.add("data-range");
  document.getElementById("dates").appendChild(wrapper);

  return (
    <>
      <DateRangeDropDownItem
        data={dataCat}
        wrapper={wrapper}
        id="dateSelectCat"
        type="cat"
        onChangeHandler={onChangeHandler}
      />
      <DateRangeDropDownItem
        data={data}
        wrapper={wrapper}
        id="dateSelectFrom"
        type="dateFrom"
        onChangeHandler={onChangeHandler}
      />
      <DateRangeDropDownItem
        data={data}
        wrapper={wrapper}
        id="dateSelectTo"
        type="dateTo"
        onChangeHandler={onChangeHandler}
      />
    </>
  );
}

function SetupDates(id) {
  //  document.getElementById();
  return "The";
}

function BarGraph() {
  const [dateFromIndex, setDateFromIndex] = useState(0);
  const [dateToIndex, setDateToIndex] = useState(0);
  const [dateCat, setDateCat] = useState(0);
  let highest = 0;
  let highestTotal = 0;
  let totalCurrent = 0;
  let totalBudget = 0;

  data.map((item) => {
    if (item.budget > highest) highest = item.budget;
    if (item.current > highest) highest = item.current;
    totalCurrent += item.current;
    totalBudget += item.budget;
  });

  highestTotal = totalCurrent > totalBudget ? totalCurrent : totalBudget;

  return (
    <>
      <BarGraphMonths data={data} highest={highest} />
      <BarGraphTotal
        highestTotal={highestTotal}
        totalCurrent={totalCurrent}
        totalBudget={totalBudget}
        categoryName={data[0].category}
      />
    </>
  );
}

export default function Reports() {
  const [data, setData] = useState();

  let baseURL = "http://localhost:3000/data.json";
  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setData(response.data);
    });
  }, [baseURL, dateFromIndex, dateToIndex, dateCat]);
  if (!data) return "error data";

  return (
    <>
      <DateRangeDropDown data={data} />
      <BarGraph data={data} />;
    </>
  );
}
