import { useState } from "react";
import React from "react";
import axios from "axios";

const useDataApi = (initialUrl, initialData) => {
  const { useState, useEffect, useReducer } = React;
  const [url, setUrl] = useState(initialUrl);

  const [state, dispatch] = useReducer(dataFetchReducer, {
    isLoading: false,
    isError: false,
    data: initialData,
  });

  useEffect(() => {
    let didCancel = false;
    const fetchData = async () => {
      dispatch({ type: "FETCH_INIT" });
      try {
        const result = await axios(url);
        if (!didCancel) {
          dispatch({ type: "FETCH_SUCCESS", payload: result.data });
        }
      } catch (error) {
        if (!didCancel) {
          dispatch({ type: "FETCH_FAILURE" });
        }
      }
    };
    fetchData();
    return () => {
      didCancel = true;
    };
  }, [url]);
  return [state, setUrl];
};

const dataFetchReducer = (state, action) => {
  switch (action.type) {
    case "FETCH_INIT":
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case "FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case "FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    default:
      throw new Error();
  }
};

function Header() {
  return (
    <div className="header-wrapper">
      <h1>Budget</h1>
      <div className="header-links">
        <a href="./UploadBalances">Upload balances</a>&nbsp;&nbsp;
        <a href="./reports">Reports</a>
      </div>{" "}
    </div>
  );
}

function Footer() {
  return (
    <div className="footer-wrapper">
      <h1>Footer</h1>
    </div>
  );
}

function Items(data, setData) {
  return Object.values(data).map((item, index) => {
    return (
      <Item
        id={item.id}
        title={item.title}
        currentBalance={item.current}
        budgetBalance={item.budget}
        arrayIndex={index}
        setData={setData}
      />
    );
  });
}

function Item({
  id,
  title,
  currentBalance,
  budgetBalance,
  arrayIndex,
  setData,
  data,
}) {
  const [edit, setEdit] = useState(false);
  let titleField = title;
  let budgetBalanceField = budgetBalance;

  let width = (currentBalance / budgetBalance) * 100;
  if (width > 99) width = 100;

  const saveEdit = (event) => {
    let localAccount = data.slice();
    localAccount[arrayIndex].title = document.getElementById(
      `${id}TitleEdit`
    ).value;
    localAccount[arrayIndex].budget = Number(
      document.getElementById(`${id}BudgetEdit`).value
    );
    setData(localAccount);
    setEdit(false);
    event.preventDefault();
  };

  if (edit) {
    titleField = (
      <>
        <input
          type="text"
          id={id + "TitleEdit"}
          key={id + "TitleEdit"}
          defaultValue={title}
          size="30"
        />
        <button
          type="submit"
          id={id + "TitleSave"}
          key={id + "TitleSave"}
          onClick={(event) => saveEdit(event)}
        >
          save
        </button>
      </>
    );
    budgetBalanceField = (
      <input
        type="number"
        id={id + "BudgetEdit"}
        key={id + "BudgetEdit"}
        defaultValue={budgetBalance}
        style={{ width: "70px" }}
      />
    );
  } else {
    titleField = title;
    budgetBalanceField = currentBalance + " of " + budgetBalance;
  }

  const handleEditClick = (event) => {
    if (edit) setEdit(false);
    else setEdit(true);
    event.preventDefault();
  };

  const handleDeleteClick = (event) => {
    let localAccount = data.slice();
    localAccount.splice(arrayIndex, 1);
    console.log(localAccount);
    setData(localAccount);
    event.preventDefault();
  };
  return (
    <>
      <div
        id={id + "WrapperDiv"}
        key={id + "WrapperDiv"}
        className="title-wrapper"
      >
        <div id={id + "Title"} key={id + "Title"} className="item-title">
          <button
            key={id + "DeleteLink"}
            className="edit-delete-button"
            onClick={(event) => handleDeleteClick(event)}
          >
            <i className="bi bi-trash"></i> &nbsp;
          </button>
          <button
            key={id + "EditLink"}
            className="edit-delete-button"
            onClick={(event) => handleEditClick(event)}
          >
            <i className="bi bi-pencil-square"></i>
          </button>
          &nbsp;
          {titleField}
        </div>
        <div id={id + "Balance"} key={id + "Balance"} className="item-balance">
          {budgetBalanceField}
        </div>
      </div>
      <div className="bar-wrapper">
        <div
          id={id + "BarCurrent"}
          key={id + "BarCurrent"}
          style={{ width: width + "%" }}
          className="item-current"
        ></div>
        <div
          id={id + "BarBudget"}
          key={id + "BarBudget"}
          style={{ width: 100 - width + "%" }}
          className="item-budget"
        ></div>
      </div>
    </>
  );
}

function Budget(data, setData) {
  return (
    <div>
      <Header key="1" />
      <Items key="2" data={data} setData={setData} />
      <Footer key="3" />
    </div>
  );
}

let baseURL = "http://localhost:3000/data.json";

export default function App() {
  const [data, setData] = useState(null);
  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setData(response.data);
    });
  }, []);

  if (!data) return null;

  return <Budget data={data} setData={setData} />;
}
