import { useState } from "react";
import React from "react";
import axios from "axios";

function BarGraphMonths({ data, highest }) {
  return data.map((item, index) => {
    return (
      <div className="wrapper-individual">
        <div
          className="actual"
          style={{ height: (item.current / highest) * 300 + "px" }}
        >
          {item.current}
        </div>
        <div
          className="budget"
          style={{ height: (item.budget / highest) * 300 + "px" }}
        >
          {item.budget}
        </div>
        <div className="date-month">{item.date}</div>
      </div>
    );
  });
}

function BarGraphTotal({ highestTotal, totalCurrent, totalBudget }) {
  return (
    <>
      <div className="overall-balance">all time balance</div>
      <div className="wrapper-total">
        <div className="actualTotal">{totalCurrent}</div>
        <div className="budgetTotal">{totalBudget}</div>
      </div>
    </>
  );
}

function BarGraph({ originalData }) {
  const [data, setData] = useState(originalData);
  let highest = 0;
  let highestTotal = 0;
  let totalCurrent = 0;
  let totalBudget = 0;
  data.map((item) => {
    if (item.budget > highest) highest = item.budget;
    if (item.current > highest) highest = item.current;
    totalCurrent += item.current;
    totalBudget += item.budget;
  });

  highestTotal = totalCurrent > totalBudget ? totalCurrent : totalBudget;

  return (
    <>
      <BarGraphMonths data={data} highest={highest} />
      <BarGraphTotal
        highestTotal={highestTotal}
        totalCurrent={totalCurrent}
        totalBudget={totalBudget}
      />
    </>
  );
}

export default function Reports() {
  let baseURL = "http://localhost:3000/dataReport.json";
  const [data, setData] = useState(null);
  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setData(response.data);
    });
  }, [baseURL]);

  if (!data) return "error";

  return <BarGraph originalData={data} />;
}
