import { useState, useEffect } from "react";
import React from "react";
import axios from "axios";

function BarGraphMonthsBar({ className, amount, highest }) {
  return (
    <div
      className={className}
      style={{ height: (amount / highest) * 300 + "px" }}
    >
      {amount}
    </div>
  );
}

function BarGraphMonths({ data, highest }) {
  return data.map((item, index) => {
    return (
      <div className="wrapper-individual">
        <BarGraphMonthsBar
          className="actual"
          amount={item.current}
          highest={highest}
        />

        <BarGraphMonthsBar
          className="budget"
          amount={item.budget}
          highest={highest}
        />
        <div className="date-month">{item.date}</div>
      </div>
    );
  });
}

function IndividualBarGraphTotal({ amount, highestTotal, className }) {
  return (
    <>
      <div
        className={className}
        style={{ width: (amount / highestTotal) * 500 + "px" }}
      >
        {amount}
      </div>
    </>
  );
}

function BarGraphTotal({
  highestTotal,
  totalCurrent,
  totalBudget,
  categoryName,
}) {
  return (
    <>
      <div className="overall-balance">
        <span className="boldfont">{categoryName}</span> - overall balance since
        category inception: ${totalBudget - totalCurrent}
      </div>
      <div className="wrapper-total">
        <IndividualBarGraphTotal
          amount={totalCurrent}
          highestTotal={highestTotal}
          className="actual-total"
          total={totalCurrent}
        />

        <IndividualBarGraphTotal
          amount={totalBudget}
          highestTotal={highestTotal}
          className="budget-total"
          total={totalBudget}
        />
      </div>
    </>
  );
}

function DateRangeDropDownItem({ data, wrapper, id, type }) {
  const element = document.getElementById(id);
  if (element == null) {
    var selectList = document.createElement("select");
    selectList.id = id;
    wrapper.appendChild(selectList);

    data.map((item, index) => {
      var option = document.createElement("option");
      option.value = index;
      if (type == "cat") option.text = item.category;
      else option.text = item.date;

      selectList.appendChild(option);
    });
  }
}

function DateRangeDropDown({ onSubmitHandler }) {
  const [dataCat, setDataCat] = useState();
  const [data, setData] = useState();

  let baseURLCat = "http://localhost:3000/dataCategories.json";
  let baseURLData = "http://localhost:3000/dataReport.json";
  React.useEffect(() => {
    axios.get(baseURLCat).then((response) => {
      setDataCat(response.data);
    });
    axios.get(baseURLData).then((response) => {
      setData(response.data);
    });
  }, [baseURLCat, baseURLData]);
  if (!dataCat) return "error with categories";
  if (!data) return "error with dates";

  let options = dataCat.map((item, index) => {
    return <option value={index}>{item.date}</option>;
  });

  var wrapper = document.createElement("div");
  wrapper.classList.add("data-range");
  document.getElementById("root").appendChild(wrapper);

  return (
    <>
      <DateRangeDropDownItem
        data={dataCat}
        wrapper={wrapper}
        id="dateSelectCat"
        type="cat"
      />
      <DateRangeDropDownItem
        data={data}
        wrapper={wrapper}
        id="dateSelectFrom"
        type="date"
      />
      <DateRangeDropDownItem
        data={data}
        wrapper={wrapper}
        id="dateSelectTo"
        type="date"
      />
      <DateRangeButton onSubmitHandler={onSubmitHandler} />
    </>
  );
}

function SetupDates(id) {
  //  document.getElementById();
  return "The";
}

function BarGraph() {
  const [data, setData] = useState();
  const [dateFromIndex, setDateFromIndex] = useState(0);
  const [dateToIndex, setDateToIndex] = useState(0);
  let highest = 0;
  let highestTotal = 0;
  let totalCurrent = 0;
  let totalBudget = 0;

  let baseURL = "http://localhost:3000/dataReport.json";
  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setData(response.data);
    });
  }, [baseURL]);

  console.log(dateToIndex);

  let currentIndex = 0;

  /*
  data.map((item, index) => {
    if (selectDate == item.date) currentIndex = index;
  });
*/

  data.map((item) => {
    if (item.budget > highest) highest = item.budget;
    if (item.current > highest) highest = item.current;
    totalCurrent += item.current;
    totalBudget += item.budget;
  });

  highestTotal = totalCurrent > totalBudget ? totalCurrent : totalBudget;

  return (
    <>
      <BarGraphMonths data={data} highest={highest} />
      <BarGraphTotal
        highestTotal={highestTotal}
        totalCurrent={totalCurrent}
        totalBudget={totalBudget}
        categoryName={data[0].category}
      />
    </>
  );
}

export default function Reports() {
  const onSubmitHandler = (event) => {
    console.log(document.getElementById(event.target.id).value);
    //    setDateFromIndex(SetupDates("dateSelectFrom"));
    //    setDateToIndex(SetupDates("dateSelectTo"));

    return (
      <>
        <BarGraph />;
      </>
    );
  };

  return (
    <>
      <DateRangeDropDown onSubmitHandler={onSubmitHandler} />
    </>
  );
}
