import { useState } from "react";

function Header() {
  return (
    <div className="header-wrapper">
      <h1>Budget</h1>
      <div className="header-links">
        <a href="./UploadBalances">Upload balances</a>&nbsp;&nbsp;
        <a href="./reports">Reports</a>
      </div>{" "}
    </div>
  );
}

function Footer() {
  return (
    <div className="footer-wrapper">
      <h1>Footer</h1>
    </div>
  );
}

function Items() {
  return account.map((item) => {
    return (
      <Item
        id={item.id}
        title={item.name}
        currentBalance={item.current}
        budgetBalance={item.budget}
      />
    );
  });
}

function Item({ id, title, currentBalance, budgetBalance }) {
  const [edit, setEdit] = useState(false);
  let titleField = title;
  let budgetBalanceField = budgetBalance;

  let width = (currentBalance / budgetBalance) * 100;
  if (width > 99) width = 100;

  if (edit) {
    titleField = (
      <input type="text" id={id + "TitleEdit"} value={title} size="30" />
    );
    budgetBalanceField = (
      <input
        type="number"
        id={id + "budgetEdit"}
        value={budgetBalance}
        size="10"
      />
    );
  } else {
    titleField = title;
    budgetBalanceField = budgetBalance;
  }

  const handleEditClick = () => {
    if (setEdit) setEdit(false);
    else setEdit(true);
    preventDefault();
  };

  return (
    <>
      <div className="title-wrapper">
        <div id={id + "Title"} key={id + "Title"} className="item-title">
          <i class="bi bi-trash"></i> &nbsp;
          <a href="" onClick={handleEditClick}>
            <i class="bi bi-pencil-square"></i>
          </a>{" "}
          &nbsp;
          {titleField}
        </div>
        <div id={id + "Balance"} key={id + "Balance"} className="item-balance">
          {currentBalance + " of " + budgetBalanceField}
        </div>
      </div>
      <div className="bar-wrapper">
        <div
          id={id + "BarCurrent"}
          key={id + "BarCurrent"}
          style={{ width: width + "%" }}
          className="item-current"
        ></div>
        <div
          id={id + "BarBudget"}
          key={id + "BarBudget"}
          style={{ width: 100 - width + "%" }}
          className="item-budget"
        ></div>
      </div>
    </>
  );
}

function Budget() {
  return (
    <div>
      <Header key="1" />
      <Items key="2" />
      <Footer key="3" />
    </div>
  );
}

const account = [
  { id: 1, current: 150, budget: 300, name: "Auto" },
  { id: 2, current: 1500, budget: 1500, name: "Home loan" },
  { id: 3, current: 750, budget: 1000, name: "Groceries" },
  { id: 4, current: 250, budget: 300, name: "Travel" },
  { id: 5, current: 500, budget: 400, name: "Misc" },
];

export default function App() {
  return <Budget />;
}
