import { useState } from "react";
import React from "react";
import axios from "axios";

function BarGraphMonthsBar({ className, amount, highest, total }) {
  return (
    <div
      className={className}
      style={{ height: (amount / highest) * 300 + "px" }}
    >
      {total}
    </div>
  );
}

function BarGraphMonths({ data, highest }) {
  return data.map((item, index) => {
    return (
      <div className="wrapper-individual">
        <div
          className="budget"
          style={{ height: (item.budget / highest) * 300 + "px" }}
        >
          {item.budget}
        </div>
        <div className="date-month">{item.date}</div>
      </div>
    );
  });
}

function IndividualBarGraphTotal({ amount, highestTotal, className, total }) {
  return (
    <>
      <div
        className={className}
        style={{ width: (amount / highestTotal) * 500 + "px" }}
      >
        {total}
      </div>
    </>
  );
}

function BarGraphTotal({
  highestTotal,
  totalCurrent,
  totalBudget,
  categoryName,
}) {
  return (
    <>
      <div className="overall-balance">
        {categoryName} - balance since category inception: $
        {totalBudget - totalCurrent}
      </div>
      <div className="wrapper-total">
        <IndividualBarGraphTotal
          amount={totalCurrent}
          highestTotal={highestTotal}
          className="actual-total"
          total={totalCurrent}
        />

        <IndividualBarGraphTotal
          amount={totalBudget}
          highestTotal={highestTotal}
          className="budget-total"
          total={totalBudget}
        />
      </div>
    </>
  );
}

function BarGraph({ originalData }) {
  const [data, setData] = useState(originalData);
  let highest = 0;
  let highestTotal = 0;
  let totalCurrent = 0;
  let totalBudget = 0;
  data.map((item) => {
    if (item.budget > highest) highest = item.budget;
    if (item.current > highest) highest = item.current;
    totalCurrent += item.current;
    totalBudget += item.budget;
  });

  highestTotal = totalCurrent > totalBudget ? totalCurrent : totalBudget;

  return (
    <>
      <BarGraphMonths data={data} highest={highest} />
      <BarGraphTotal
        highestTotal={highestTotal}
        totalCurrent={totalCurrent}
        totalBudget={totalBudget}
        categoryName={data[0].category}
      />
    </>
  );
}

export default function Reports() {
  let baseURL = "http://localhost:3000/dataReport.json";
  const [data, setData] = useState(null);
  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setData(response.data);
    });
  }, [baseURL]);

  if (!data) return "error";

  return <BarGraph originalData={data} />;
}
