import { useState } from "react";
import React from "react";
import axios from "axios";

function BarGraph({ originalData }) {
  const [data, setData] = useState(originalData);
  let highest = 0;
  data.map((item) => {
    if (item.budget > highest) highest = item.budget;
    if (item.current > highest) highest = item.current;
  });

  return data.map((item, index) => {
    return (
      <>
        <div className="wrapper-individual">
          <div
            className="actual"
            style={{ height: (item.current / highest) * 300 + "px" }}
          >
            {item.current}
          </div>
          <div
            className="budget"
            style={{ height: (item.budget / highest) * 300 + "px" }}
          >
            {item.budget}
          </div>
          <div className="date-month">5/2023</div>
        </div>
      </>
    );
  });
}

export default function Reports() {
  let baseURL = "http://localhost:3000/dataReport.json";
  const [data, setData] = useState(null);
  React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setData(response.data);
    });
  }, [baseURL]);

  if (!data) return "error";

  return <BarGraph originalData={data} />;
}
