import { useState } from "react";

function ProductCategoryRow({ category }) {
  return (
    <tr>
      <th colSpan="2">{category}</th>
    </tr>
  );
}

function ProductRow({ product }) {
  const name = product.stocked ? (
    product.name
  ) : (
    <span style={{ color: "red" }}>{product.name}</span>
  );

  return (
    <tr>
      <td>{name}</td>
      <td>{product.price}</td>
    </tr>
  );
}

function ProductTable({ products }) {
  const rows = [];
  let lastCategory = null;

  products.forEach((product) => {
    if (product.category !== lastCategory) {
      rows.push(
        <ProductCategoryRow
          category={product.category}
          key={product.category}
        />
      );
    }
    rows.push(<ProductRow product={product} key={product.name} />);
    lastCategory = product.category;
  });

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Price</th>
        </tr>
      </thead>
      <tbody>{rows}</tbody>
    </table>
  );
}

function SearchBar({ onChangeCheckbox, onChangeInput }) {
  return (
    <form>
      <input type="text" placeholder="Search..." onChange={onChangeInput} />
      <label>
        <input type="checkbox" onChange={onChangeCheckbox} /> Only show products
        in stock
      </label>
    </form>
  );
}

function FilterableProductTable({ products }) {
  const [filteredProducts, setFilteredProducts] = useState(products);

  const filterInstock = () => {
    const result = filteredProducts.filter((item) => item.stocked);
    setFilteredProducts(result);
  };

  const filterKeyWord = (event) => {
    const result = filteredProducts.filter((item) => {
      console.log(event.target.value);
      if (event.target.value != "") {
        return item.name.includes(event.target.value);
        alert("HERE2");
      } else {
        return true;
      }
    });
    setFilteredProducts(result);
  };

  return (
    <div>
      <SearchBar
        onChangeCheckbox={filterInstock}
        onChangeInput={(event) => filterKeyWord(event)}
      />
      <ProductTable products={filteredProducts} />
    </div>
  );
}

const PRODUCTS = [
  { category: "Fruits", price: "$1", stocked: true, name: "Apple" },
  { category: "Fruits", price: "$1", stocked: true, name: "Dragonfruit" },
  { category: "Fruits", price: "$2", stocked: false, name: "Passionfruit" },
  { category: "Vegetables", price: "$2", stocked: true, name: "Spinach" },
  { category: "Vegetables", price: "$4", stocked: false, name: "Pumpkin" },
  { category: "Vegetables", price: "$1", stocked: true, name: "Peas" },
];

export default function App() {
  return <FilterableProductTable products={PRODUCTS} />;
}
