import { useState, useEffect } from "react";
import React from "react";
import axios from "axios";
import { useSearchParams } from "react-router-dom";
import { format } from "date-fns";
import HeaderLinks from "./HeaderLinks.js";

function Header() {
  return (
    <div className="header-wrapper">
      <h1>Reports</h1>
      <HeaderLinks />
    </div>
  );
}

function BarGraphMonthsBar({ className, amount, highest }) {
  return (
    <div
      className={className}
      style={{ height: (amount / highest) * 300 + "px" }}
    >
      {amount}
    </div>
  );
}

function BarGraphMonths({ data, highest }) {
  let itemDate = "";
  let e = document.getElementById("dateSelectFrom");
  let textFrom = e.options[e.selectedIndex].text;
  textFrom = Date.parse(textFrom);

  e = document.getElementById("dateSelectTo");
  let textTo = e.options[e.selectedIndex].text;
  textTo = Date.parse(textTo);

  return data.map((item, index) => {
    itemDate = Date.parse(item.date);
    if (itemDate >= textFrom && itemDate <= textTo) {
      return (
        <div className="wrapper-individual">
          <BarGraphMonthsBar
            className="actual"
            amount={item.current}
            highest={highest}
          />

          <BarGraphMonthsBar
            className="budget"
            amount={item.budget}
            highest={highest}
          />
          <div className="date-month">
            {format(Date.parse(item.date), "M/yyyy")}
          </div>
        </div>
      );
    }
  });
}

function IndividualBarGraphTotal({ amount, highestTotal, className }) {
  return (
    <>
      <div
        className={className}
        style={{ width: (amount / highestTotal) * 500 + "px" }}
      >
        {amount}
      </div>
    </>
  );
}

function BarGraphTotal({
  highestTotal,
  totalCurrent,
  totalBudget,
  categoryName,
}) {
  return (
    <>
      <div className="overall-balance">
        <span className="boldfont">{categoryName}</span> - overall balance since
        category inception: ${totalBudget - totalCurrent}
      </div>
      <div className="wrapper-total">
        <IndividualBarGraphTotal
          amount={totalCurrent}
          highestTotal={highestTotal}
          className="actual-total"
          total={totalCurrent}
        />

        <IndividualBarGraphTotal
          amount={totalBudget}
          highestTotal={highestTotal}
          className="budget-total"
          total={totalBudget}
        />
      </div>
    </>
  );
}

function DateRangeDropDownItem({ data, id, onChangeHandler }) {
  let currentDateMonth = "";
  let currentDateYear = "";
  let dateToFind = "";
  let label = "From";

  if (id == "dateSelectFrom") {
    currentDateMonth = format(new Date(), "M");
    currentDateYear = String(Number(format(new Date(), "yyyy")) - 1);
    dateToFind = currentDateMonth + "/1/" + currentDateYear;
  } else {
    currentDateMonth = format(new Date(), "M");
    currentDateYear = format(new Date(), "yyyy");
    dateToFind = currentDateMonth + "/1/" + currentDateYear;
    label = "To";
  }

  const loadOptions = () => {
    let result = data.map((item) => {
      if (item.date == dateToFind)
        return (
          <option value={item.date} selected>
            {item.date}
          </option>
        );
      else return <option value={item.date}>{item.date}</option>;
    });
    return result;
  };

  return (
    <div className="date-item-wrapper">
      <label key={id + "-Label"} htmlFor={id} className="label top-padding">
        {label}
        <select
          id={id}
          name={id}
          key={id}
          className="form-control"
          onChange={onChangeHandler}
        >
          {loadOptions()}
        </select>
      </label>
    </div>
  );
}

function DateRangeDropDownGroupItem({ data, setDateCat, onChangeHandler }) {
  const [searchParams, setSearchParams] = useSearchParams();

  const loadOptions = () => {
    let result = data.map((item) => {
      if (
        searchParams.get("category") != null &&
        item.category == searchParams.get("category")
      ) {
        setDateCat(item.category);
        return (
          <option value={item.id} selected>
            {item.category}
          </option>
        );
      } else {
        return <option value={item.id}>{item.category}</option>;
      }
    });
    return result;
  };

  return (
    <div className="date-item-wrapper">
      <label
        key="DateSelectCat-Label"
        htmlFor="DateSelectCat"
        className="label top-padding"
      >
        Select Group
        <select
          id="DateSelectCat"
          name="DateSelectCat"
          key="DateSelectCat"
          className="form-control"
          onChange={onChangeHandler}
        >
          {loadOptions()}
        </select>
      </label>
    </div>
  );
}

function DateRangeDropDownCat({ setDateCat }) {
  const onChangeHandlerCat = () => {
    alert("Cat change");
  };

  return (
    <>
      <DateRangeDropDownGroupItem
        data={dataCat}
        setDateCat={setDateCat}
        onChangeHandler={onChangeHandlerCat}
      />
    </>
  );
}

function DateRangeDropDownDates({ dataReport }) {
  const onChangeHandlerDate = (event) => {
    alert("Date changed");
  };

  return (
    <>
      <DateRangeDropDownItem
        data={dataReport}
        id="dateSelectFrom"
        onChangeHandler={onChangeHandlerDate}
      />
      <DateRangeDropDownItem
        data={dataReport}
        id="dateSelectTo"
        onChangeHandler={onChangeHandlerDate}
      />
    </>
  );
}

function BarGraph({ data }) {
  let highest = 0;
  let highestTotal = 0;
  let totalCurrent = 0;
  let totalBudget = 0;

  let e = document.getElementById("dateSelectFrom");
  let textFrom = e.options[e.selectedIndex].text;
  textFrom = Date.parse(textFrom);

  e = document.getElementById("dateSelectTo");
  let textTo = e.options[e.selectedIndex].text;
  textTo = Date.parse(textTo);

  data.map((item) => {
    if (Date.parse(item.date) >= textFrom && Date.parse(item.date) <= textTo) {
      if (item.budget > highest) highest = item.budget;
      if (item.current > highest) highest = item.current;
    }
    totalCurrent += item.current;
    totalBudget += item.budget;
  });

  highestTotal = totalCurrent > totalBudget ? totalCurrent : totalBudget;
  e = document.getElementById("dateSelectCat");

  return (
    <>
      <BarGraphMonths data={data} highest={highest} />
      <BarGraphTotal
        highestTotal={highestTotal}
        totalCurrent={totalCurrent}
        totalBudget={totalBudget}
        categoryName={e.options[e.selectedIndex].text}
      />
    </>
  );
}

export default function Reports() {
  const [url, setUrl] = useState("http://localhost:3000/dataReport.json?");
  const [data, setData] = useState(null);
  const [dataCat, setDataCat] = useState();
  const [dateFrom, setDateFrom] = useState();
  const [dateTo, setDateTo] = useState();

  let baseURLCat = "http://localhost:3000/dataCategories.json";
  React.useEffect(() => {
    axios.get(url).then((response) => {
      setData(response.data);
    });
    axios.get(baseURLCat).then((response) => {
      setDataCat(response.data);
    });
  }, [url]);
  if (!data) return "error data";

  return (
    <>
      <Header />
      <div
        id="ReportDatesWrapper"
        key="ReportDatesWrapper"
        className="report-dates-well left-indent card card-body bg-light"
      >
        <div key="ReportDatesWell">
          <DateRangeDropDownCat dataCat={dataCat} setDataCat={setDataCat} />
          <DateRangeDropDownDates
            dataReport={data}
            setDateFrom={setDateFrom}
            setDateTo={setDateTo}
          />
          <BarGraph data={data} />;
        </div>
      </div>
    </>
  );
}
